mapper.select:
select		count(REV_NUM)
from	  		REVIEW
where		PRD_NUM=#{prdNum}

select REV_NUM, NAME, REV_WRITE_DATE, REV_TITLE, REV_CONT, REV_HITS from (select ROW_NUMBER() OVER (
ORDER BY r.REV_WRITE_DATE desc
ORDER BY r.REV_HITS asc
ORDER BY r.REV_HITS desc
ORDER BY r.REV_WRITE_DATE asc
) as rnum, r.REV_NUM, m.NAME, r.REV_WRITE_DATE, r.REV_TITLE, r.REV_CONT, r.REV_HITS from REVIEW r, MEMBER m where m.id=r.id(+) and r.PRD_NUM=#{prdNum}
ORDER BY r.REV_WRITE_DATE desc
ORDER BY r.REV_HITS asc
ORDER BY r.REV_HITS desc
ORDER BY r.REV_WRITE_DATE asc
) WHERE RNUM BETWEEN #{startNum} AND #{endNum}

mapper.update:
update	 REVIEW
set		 REV_HITS=REV_HITS+1
where	 REV_NUM=#{revNum}

update	ORDER_DETAIL
set		 REV_FLAG='1'
where	 ORDER_NUM=#{orderNum} AND PRD_NUM=#{prdNum}
