mapper.select:
select PRD_NUM, THUM, PRD_NAME, PRD_REGIST_DATE, PRD_PRICE, PRD_DISCOUNT, MAIN_CATE_NAME, SUB_CATE_NAME from (select ROW_NUMBER() OVER (ORDER BY
p.PRD_PRICE ASC
p.PRD_PRICE DESC
p.PRD_REGIST_DATE DESC
) as rnum, p.PRD_NUM, p.THUM, p.PRD_NAME, p.PRD_PRICE, p.PRD_DISCOUNT, p.PRD_REGIST_DATE, cm.MAIN_CATE_NAME, cs.SUB_CATE_NAME from PRODUCT p, CATEGORY_MAIN cm, CATEGORY_SUB cs where (cs.SUB_CATE_NUM=p.SUB_CATE_NUM AND cm.MAIN_CATE_NUM=cs.MAIN_CATE_NUM) AND p.PRD_NAME LIKE '%'||#{keyword}||'%'
ORDER BY p.PRD_PRICE ASC
ORDER BY p.PRD_PRICE DESC
ORDER BY p.PRD_REGIST_DATE DESC
) WHERE RNUM BETWEEN #{startNum} AND #{endNum}
